import styled from 'styled-components'

const Dropdown = styled.select`
	background-color: transparent;
	width: 160px;
	border: none;
	font-weight: 600;
	outline: none;
	padding: 15px;
	height: 100%;
	margin-bottom: 30;
	color:#003CFF;
	-webkit-appearance: none;
	-moz-appearance: none;
`;

export default Dropdown;
