import React from 'react'
import { Image, Layout, Menu } from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  UserOutlined,
  VideoCameraOutlined,
  UploadOutlined,
} from '@ant-design/icons';

const { Header, Sider, Content } = Layout;

class AdminPage extends React.Component {
  state = {
    collapsed: false,
  };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  };
  render() {
    return (
      <Layout className="full-screen">
        <Sider width={250} className='light-bg' trigger={null} collapsible collapsed={this.state.collapsed}>
			<div className='logo'>
				<Image src='./logo.png' />
			</div>
			<div className='profile'>
				<div className='profile-pic'>
				</div>
				<div class>
					<h3>Kazem</h3>
					<p>Superadmin</p>
				</div>
			</div>
			<Menu
				className='menu-fix'
				theme="light"
				mode="inline"
				defaultSelectedKeys={['1']}
				items={[
				{
					key: '1',
					icon: <UserOutlined />,
					label: 'Dashboard',
				},
				{
					key: '2',
					icon: <VideoCameraOutlined />,
					label: 'Search Job',
				},
				{
					key: '3',
					icon: <UploadOutlined />,
					label: 'Application',
				},
				{
					key: '4',
					icon: <UploadOutlined />,
					label: 'Message',
				},
				{
					key: '5',
					icon: <UploadOutlined />,
					label: 'Statistics',
				},
				]}
			/>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 0 }}>
            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
              className: 'trigger',
              onClick: this.toggle,
            })}
          </Header>
          <Content
            className="site-layout-background"
            style={{
              margin: '24px 16px',
              padding: 24,
              minHeight: 280,
            }}
          >
            Content
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default AdminPage;
